// Types
export type TourCategory = 'all' | 'adventure' | 'family' | 'couple' | 'luxury' | 'cultural' | 'wellness'
export type BudgetRange = 'all' | '0-25' | '25-50' | '50-100' | '100+'

export interface Tour {
  id: string
  name: string
  description: string
  category: Exclude<TourCategory, 'all'>
  duration: string
  price: number
  image: string
  images: string[]
  highlights: string[]
  rating: number
}

export interface Resort {
  id: string
  name: string
  location: string
  description: string
  pricePerNight: number
  budgetRange: Exclude<BudgetRange, 'all'>
  image: string
  images: string[]
  amenities: string[]
  rating: number
  tag?: string
}

export interface CountryData {
  name: string
  tagline: string
  description: string
  heroImage: string
  icon: string
  tours: Tour[]
  resorts: Resort[]
}

