// Helper function to get country data
export const getCountryData = (country: string): CountryData | null => {
  const countryMap: Record<string, CountryData> = {
    thailand: thailandData,
    maldives: maldivesData,
    indonesia: indonesiaData,
    vietnam: vietnamData,
    japan: japanData,
  }
  return countryMap[country.toLowerCase()] || null
}

// Helper function to filter resorts by budget
export const filterResortsByBudget = (resorts: Resort[], budget: BudgetRange): Resort[] => {
  if (budget === 'all') return resorts
  return resorts.filter(r => r.budgetRange === budget)
}

// Helper function to filter tours by category
export const filterToursByCategory = (tours: Tour[], category: TourCategory): Tour[] => {
  if (category === 'all') return tours
  return tours.filter(t => t.category === category)
}
